{
    "request": {
        "name": "Linked Content",
        "searchQuery": {
            "request": {
                "facets": [
                    "language",
                    "grade",
                    "domain",
                    "contentType",
                    "primaryCategory",
                    "subject",
                    "medium"
                ],
                "filters": {
                    "primaryCategory": [
                        "Digital Textbook",
                        "Textbook Unit",
                        "Course",
                        "Explanation Content",
                        "Content Playlist"
                    ],
                    "contentType": [
                        "Collection",
                        "TextBook",
                        "TextBookUnit",
                        "Resource",
                        "Course"
                    ],
                    "status": [
                        "Live"
                    ],
                    "compatibilityLevel": {
                        "max": 4,
                        "min": 1
                    },
                    "visibility": []
                },
                "mode": "collection",
                "userProfile": {}
            },
            "limit": 10,
            "sort_by": {
                "lastUpdatedOn": "desc"
            }
        },
        "sectionDataType": "Content",
        "display": {
            "name": {
                "en": "Linked Content"
            }
        }
    }
}
